              </div>
              <div className="SelectBox">
                <select onChange={(e: React.ChangeEvent<HTMLSelectElement>) =>{ setENName(e.target.value)}} defaultValue={EN[0].name}>
                  { EN.map((item: Pro) => (
                    <option key={ item.id }>{ item.name }</option>
                  )) }
                </select>
                <button onClick={clickAddEN}>Enter</button>
              </div>
            </div>
          </div>
          <div className="Result">
            <h1 className="title">{ message }</h1>
            <div className="Result__Border">
              { plan.map((item, idx: number) => (
                <div className="Flex" key={idx}>
                  <div className="ResultIndex">{idx}</div>
                  <div className="ResultName">{item.name}</div>
                  <div className="ResultTime">{item.time}</div>
                  <button className="AddCount" onClick={()=> addTime(idx)}>-0.25</button>
                  <button className="AddCount">+0.25</button>
                  <button className="AddCount">+1.00</button>
                  <button className="DeleteButton" onClick={()=> handleRemoveTask(idx)}>Delete</button>
                </div>
              )) }
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}


export default Process;
